func hashFromString(s string) bitcoin.Hash {
	hash, err := bitcoin.NewHashFromString(
		s,
		bitcoin.ReversedByteOrder,
	)
	if err != nil {
		panic(err)
	}

	return hash
}

func decodeString(s string) []byte {
	bytes, err := hex.DecodeString(s)
	if err != nil {
		panic(err)
	}

	return bytes
}
